import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:main/models/products.dart';import 'package:main/pages/home.dart';import 'package:main/pages/loginScreen.dart';import 'package:main/providers/cartProvider.dart';import 'package:main/providers/product_provider.dart';import 'package:main/providers/user_provider.dart';import 'package:main/splashScreen.dart';import 'package:main/values.dart';import 'package:main/wrapper.dart';import 'package:provider/provider.dart';import 'package:simple_splashscreen/simple_splashscreen.dart';void main() {  runApp(MyApp());}class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    SystemChrome.setPreferredOrientations([      DeviceOrientation.portraitUp,    ]);    return MultiProvider(        providers: [          ChangeNotifierProvider<getProducts>(create: (_)=>getProducts()),          ChangeNotifierProvider<user>(create: (_)=>user()),          ChangeNotifierProvider<cartProvider>(create: (_)=>cartProvider(),)        ],        child: MaterialApp(      theme: ThemeData(        primaryColor: mainColor,      ),      title: 'FlipFast',        home:Simple_splashscreen(          timerInSeconds: 3,          gotoWidget: wrapper(),          splashscreenWidget: splashScreen(),          context: context,        )    ));  }}